#include <bits/stdc++.h>
#define ri register int
#define ll long long
#define db double
using namespace std;

int read()
{
	int num=0;
	int flg=1;
	char c=getchar();
	while(!isdigit(c))
	{
		if(c=='-')
		{
			flg=-1;
		}
		c=getchar();
	}
	while(isdigit(c))
	{
		num=(num<<1)+(num<<3)+(c^48);
		c=getchar();
	}
	return num*flg;
}

const int maxn=1e6+7;

int n;
int dfn[maxn];
int red[maxn];//redfn

/*
dfn[red[i]]=i;
red[dfn[i]]=i;
秘技：下标交换术
*/

int dif[maxn];
db ans=1.0;

//bfn就不需要了，因为替换成了自然数序列
//并且同时对dfs序重标号
int main()
{
	n=read();
	dif[1]++;
	dif[2]--;
	//根节点总是要分层的，这点容易忽略。
	for(ri i=1;i<=n;i++)
	{
		dfn[read()]=i;
	}//交换下标和值，方便进行换序
	for(ri i=1;i<=n;i++)
	{
		red[dfn[read()]]=i;
		//printf("q:%d",dfn[i]);
	}//bfs序重标号
	for(ri i=1;i<=n;i++)
	{
		dfn[red[i]]=i;
	}//dfs序重标号
	for(ri i=1;i<=n-1;i++)
	{
		if(dfn[i]>dfn[i+1])
		{
			ans++;
			dif[i]++;
			dif[i+1]--;
		}//打标记，0.5的贡献只产生一次
		if(red[i+1]-1>red[i])
		{
			dif[red[i]]++;
			dif[red[i+1]]--;
		}//差分
	}
	int pre=0;//扫一遍差分数组，判断该位置是否确定
	for(ri i=1;i<=n-1;i++)//最多分n-1层
	{
		pre+=dif[i];
		if(!pre)
		{
			ans+=0.5;
		}
	}
	ans++;//depth=seg_amount+1;
	return 0&printf("%.3lf",ans);
}
